import React, { useState } from "react";
import io from "socket.io-client";
import Slider from "@material-ui/core/Slider";
import styled from "styled-components";

const endpoint = "http://localhost:3005/";
const socket = io(endpoint);

const Main = styled.div`
  height: 90%;
  width: 90%;
`;

const MainContainer = styled.div`
  position: relative;
  height: 100vh;
  overflow: hidden;
`;

const Animated = styled(animated.div)`
  position: absolute;
  width: 100%;
  max-height: ${OPEN_NAV_SIZE * windowHeight}px;
  height: 100%;
`;

const Overflow = styled.div`
  position: absolute;
  width: 100%;
  bottom: 0px;
  top: 0;
  background: linear-gradient(135deg, #c3cfe2, #c3cfe2);
`;

const Home = () => {
  const [number, setNumber] = useState(0);

  const handleSliderChange = (event, newNumber) => {
    setNumber(newNumber);
    event.preventDefault();
    socket.emit("outgoing", number);
  };

  const PullContainer = ({ children, overflowHeight }) => {
    const trans = y => `translateY(${y}px)`;
    const bottom = px => `-${px}px`;

    const [styleProps, set] = useSpring(() => ({
      y: 0,
      bottom: overflowHeight,
      config: config.stiff
    }));
    const bind = useDrag(({ down, movement: [x, y], initial }) => {
      let bottom = overflowHeight;
      const percent = (-y * 100) / windowHeight;
      let setY = y;
      if (!down && percent > 30) {
        setY = -OPEN_NAV_SIZE * windowHeight;
        bottom = OPEN_NAV_SIZE * windowHeight;
      } else if (!down) {
        setY = 0;
      }
      set({
        y: setY,
        bottom
      });
    });

    return (
      <MainContainer>
        <Animated
          {...bind()}
          style={{
            transform: styleProps.y.to(trans),
            bottom: styleProps.bottom.to(bottom)
          }}
        >
          {children}
        </Animated>
      </MainContainer>
    );
  };

  return (
    <Main>
      <label>
        Testing:
        <Slider
          defaultValue={0}
          valueLabelDisplay="auto"
          step={0.1}
          marks
          min={0}
          max={1}
          onChange={handleSliderChange}
        />
      </label>
      <PullContainer />
    </Main>
  );
};

export default Home;
