import React, { useRef, useState, useEffect, useMemo } from "react";
import * as THREE from "three";
import { useFrame, useThree } from "react-three-fiber";
import io from "socket.io-client";
import { Canvas as c } from "react-three-fiber";
import styled from "styled-components";

const endpoint = "http://localhost:3005/";
const socket = io(endpoint);

function Content({ props, thing }) {
  // This reference will give us direct access to the mesh
  const mesh = useRef();
  const { size, viewport } = useThree();
  const aspect = size.width / viewport.width;

  const dummy = useMemo(() => new THREE.Object3D(), []);

  // Set up state for the hovered and active state
  const [hovered, setHover] = useState(false);
  const [active, setActive] = useState(false);

  const particles = useMemo(() => {
    const temp = [];
    for (let i = 0; i < thing; i++) {
      const t = Math.random() * 100;
      const factor = 20 + Math.random() * 100;
      const speed = 0.01 + Math.random() / 200;
      const xFactor = -50 + Math.random() * 100;
      const yFactor = -50 + Math.random() * 100;
      const zFactor = -50 + Math.random() * 100;
      temp.push({ t, factor, speed, xFactor, yFactor, zFactor, mx: 0, my: 0 });
    }
    return temp;
  }, [thing]);

  useFrame(state => {
    particles.forEach((particle, i) => {
      let { t, factor, speed, xFactor, yFactor, zFactor } = particle;
      particle.mx += (thing - particle.mx) * 0.01;
      particle.my += (thing - particle.my) * 0.01;
    });
  });

  return (
    <mesh
      {...props}
      ref={mesh}
      scale={active ? [50, 50, 50] : [100, 100, 100]}
      onClick={e => setActive(!active)}
      onPointerOver={e => setHover(true)}
      onPointerOut={e => setHover(false)}
    >
      <boxBufferGeometry attach="geometry" args={[1, 1, 1]} />
      <meshStandardMaterial
        attach="material"
        color={hovered ? "hotpink" : "orange"}
        roughness={0.75}
        metalness={0.6}
      />
    </mesh>
  );
}

function Lights() {
  return (
    <group>
      <pointLight intensity={0.3} />
      <ambientLight intensity={2} />
      <spotLight
        castShadow
        intensity={0.2}
        angle={Math.PI / 7}
        position={[150, 150, 250]}
        penumbra={1}
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
    </group>
  );
}

const Canvas = styled(c)`
  position: absolute !important;
  margin: 0;
  padding: 0;
`;

export default function Box() {
  const [thing, setThing] = useState(0);
  useEffect(() => {
    socket.on("three", data => {
      setThing(data);
    });
  }, []);
  console.log("state number " + thing);

  return (
    <Canvas
      shadowMap
      camera={{ position: [0, 0, 100], fov: 100 }}
      gl={({ gl }) => {
        gl.toneMapping = THREE.ACESFilmicToneMapping;
        gl.outputEncoding = THREE.sRGBEncoding;
      }}
    >
      <Lights />
      <Content thing={thing} />
    </Canvas>
  );
}
